CREATE DATABASE IF NOT EXISTS quan_ly_thu_vien;
USE quan_ly_thu_vien;
CREATE TABLE IF NOT EXISTS quyen (
	MaQuyen int PRIMARY KEY AUTO_INCREMENT,
    Quyen varchar(20) NOT NULL
);
CREATE TABLE IF NOT EXISTS tai_khoan (
  	MaTK int PRIMARY KEY AUTO_INCREMENT,
  	SoDienThoai varchar(10) NOT NULL,
  	MatKhau varchar(50) NOT NULL,
  	HoTen varchar(50) NOT NULL,
  	HinhAnh varchar(50),
    NgaySinh date,
  	ViTien int NOT NULL DEFAULT 0,
  	MaQuyen int NOT NULL DEFAULT 1,
   	FOREIGN KEY (MaQuyen) REFERENCES quyen(MaQuyen) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS chu_de (
  	MaCD int PRIMARY KEY AUTO_INCREMENT,
  	TenChuDe varchar(255) NOT NULL
); 

CREATE TABLE IF NOT EXISTS sach (
  MaSach int PRIMARY KEY AUTO_INCREMENT,
  TuaSach varchar(255) NOT NULL,
  HinhAnh varchar(50) NOT NULL,
  TacGia varchar(255) NOT NULL,
  GiaTri int NOT NULL,
  MoTa text,
  MaCD int,
  SoLuong int NOT NULL,
  GhimTrangChu bit(1) DEFAULT b'0',
  SoCamNghi int DEFAULT 0,
  LuotDoc int DEFAULT 0,
  LuotXem int DEFAULT 0,
  FOREIGN KEY (MaCD) REFERENCES chu_de(MaCD) ON DELETE CASCADE ON UPDATE CASCADE
); 
-- '1: ghim, 0: không ghim'
-- Lượt đọc dựa theo số lần mượn
-- Dựa theo số lần bấm vào


CREATE TABLE IF NOT EXISTS trang_thai(
	MaTT int PRIMARY KEY AUTO_INCREMENT,
	TenTrangThai varchar(50) NOT NULL
);
CREATE TABLE lich_su_muon (
  	MaLS int PRIMARY KEY AUTO_INCREMENT,
  	MaTK int NOT NULL,
  	NgayMuon date NOT NULL,
  	NgayTra date NOT NULL,
  	SoSachMuon int NOT NULL DEFAULT 0,
  	TongTien int NOT NULL DEFAULT 0,
  	MaTT int NOT NULL DEFAULT 1,
    FOREIGN KEY (MaTK) REFERENCES tai_khoan(MaTK) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (MaTT) REFERENCES trang_thai(MaTT) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS chi_tiet_lich_su_muon (
    MaChiTiet int PRIMARY KEY AUTO_INCREMENT,
  	MaLS int NOT NULL,
  	MaSach int NOT NULL,
    SoLuong int NOT NULL,
    FOREIGN KEY (MaLS) REFERENCES lich_su_muon(MaLS) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (MaSach) REFERENCES sach(MaSach) ON DELETE CASCADE ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS cam_nghi(
    MaCN int PRIMARY KEY AUTO_INCREMENT,
    MaTK int NOT NULL,
    MaSach int NOT NULL,
    NoiDung varchar(255) NOT NULL,
    NgayGui datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (MaTK) REFERENCES tai_khoan(MaTK) ON DELETE CASCADE ON UPDATE CASCADE,
	FOREIGN KEY (MaSach) REFERENCES sach(MaSach) ON DELETE CASCADE ON UPDATE CASCADE
);




